import React from "react";
import heropic from "./../media/heropic.svg";
import styled, { createGlobalStyle } from "styled-components";
import importGoogleFonts from "import-google-fonts";

const GoogleFonts = importGoogleFonts(createGlobalStyle, null, [
  "Abril Fatface",
  "Gotu",
]);

const Header = styled.h2`
  font-family: "Abril Fatface";
  font-size: 90px;
  font-weight: 400;
  line-height: 94px;
  color: white;
`;
const Slogan = styled.p`
  font-family: "System-ui";
  font-weight: 100;
  font-size: 40px;
  text-transform: uppercase;
  color: #ffffff;
`;
const PageDiv = styled.div`
  display: flex;
  align-content: center;
  justify-content: space-around;
  margin: 20px;
`;
const TextDiv = styled.div`
  align-content: center;
  justify-content: center;
`;
const MediaDiv = styled.div`  
display: flex;
flex-wrap; wrap;
align-content: center;
justify-content: center;
`;
const HeroPic = styled.img`
  height: 500px;
`;
const Button = styled.button`
background-image: linear-gradient(to right, #fc00ff 0%, #00dbde  51%, #fc00ff  100%);
margin: 10px;
padding: 15px 45px;
text-align: center;
text-transform: uppercase;
transition: 0.5s;
background-size: 200% auto;
color: white;            
   /* box-shadow: 0 0 5px #eee; */
border-radius: 10px;
/* display: block; */

/* @hover {
   background-position: right center; /* change the direction of the change here */
   color: #fff;
   text-decoration: none;
 } */
`;
export default function Hero() {
  return (
    <PageDiv>
      <TextDiv>
        <GoogleFonts />
        <Header>Digi-Book</Header>
        <Slogan>Your Digitial Note Pad</Slogan>
      </TextDiv>
      <MediaDiv>
        <HeroPic src={heropic}></HeroPic>
        <Button></Button>
      </MediaDiv>
    </PageDiv>
  );
}
