export default (posts = [], action) => {
  switch (action.type) {
    case "UPDATE":
      return post.map((post) =>
        post_id === action.payload._id ? action.payload : post
      );
    //map over to get a new array from existing and if the id is the same as the new id, then return the new id
    case "FETCH_ALL":
      return action.payload;
    case "CREATE":
      return [...posts, action.payload];
    default:
      return posts;
  }
};
// export default reducer
//action.payload= our posts
