import React from "react";
import heropic from "./../media/heropic.svg";
import styled, { createGlobalStyle } from "styled-components";
import importGoogleFonts from "import-google-fonts";

const GoogleFonts = importGoogleFonts(createGlobalStyle, null, [
  "Abril Fatface",
  "Gotu",
]);

const Header = styled.h2`
  font-family: "Abril Fatface";
  font-size: 70px;
  font-weight: 200;
  line-height: 94px;
  color: white;
`;
const Slogan = styled.p`
  font-family: "System-ui";
  font-weight: 100;
  font-size: 40px;
  text-transform: uppercase;
  color: #ffffff;
`;
const PageDiv = styled.div`
  display: flex;
  align-content: center;
  justify-content: center;
`;
const TextDiv = styled.div`
  align-content: center;
  justify-content: center;
`;
const MediaDiv = styled.div``;
const HeroPic = styled.img`
  height: 700px;
`;
export default function Hero() {
  return (
    <PageDiv>
      <TextDiv>
        <GoogleFonts />
        <Header>Digi-Book</Header>
        <Slogan>Your Digitial Note Pad</Slogan>
      </TextDiv>
      <MediaDiv>
        <HeroPic src={heropic}></HeroPic>
      </MediaDiv>
    </PageDiv>
  );
}
